mapper.select:
select board_idx, board_writer, board_title, board_contents, board_parent,
board_seq, board_view_count, file_idx
from sample_board_tb

	 and board_idx=#{boardIdx} 				
	 and board_writer=#{boardWriter} 			
	 and board_title=#{boardTitle} 			
	 and board_contents=#{boardContents} 		
	 and board_parent=#{boardParent} 			
	 and board_seq=#{boardSeq} 				
	 and board_view_count=#{boardViewCount} 	
	 and file_idx=#{fileIdx} 					

 order by board_idx desc 
 limit #{offset}, #{row}
